class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        four_flips = 0
        for row in range(m//2):
            for col in range(n//2):
                num_ones = 0
                if grid[row][col]:
                    num_ones += 1
                if grid[row][n-1-col]:
                    num_ones += 1
                if grid[m-1-row][n-1-col]:
                    num_ones += 1
                if grid[m-1-row][col]:
                    num_ones += 1
                four_flips += min(4-num_ones, num_ones)
        two_flips = 0
        twosum = 0
        if m % 2 == 1:
            for col in range(n//2):
                if grid[m//2][col] != grid[m//2][n-1-col]:
                    two_flips += 1
                else:
                    twosum += 2*grid[m//2][col]
        if n % 2 == 1:
            for row in range(m//2):
                if grid[row][n//2] != grid[m-1-row][n//2]:
                    two_flips += 1
                else:
                    twosum += 2*grid[row][n//2]
        if not two_flips and twosum % 4 != 0:
            two_flips += 2
        center_flip = 0
        if m % 2 == 1 and n % 2 == 1:
            if grid[m//2][n//2]:
                center_flip += 1
        return four_flips + two_flips + center_flip
