class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int cnt = 0;
        int m = grid.size();
        int n = grid[0].size();
        int l=0;
        int r=m-1;
        int l2,r2;
        int cnt1p=0;
        int cnt0p=0;
        int cnt01=0;
        while(l<=r){
            l2 = 0;
            r2 = n-1;
            while(l2<=r2){
                if(l2==r2 && l==r){
                    if(grid[l][l2]==1) {cnt++;};
                } 
                else{
                    if(l2==r2 || l==r){
                        int a = grid[l][l2];
                        int d = grid[r][r2];
                        if(a==d){
                            if(a==0){
                                cnt0p++;
                            }
                            else{
                                cnt1p++;
                            }
                        }   
                        else{
                            cnt01++;
                        }

                    }
                    else{
                        int a = grid[l][l2];
                        int b = grid[l][r2];
                        int c = grid[r][l2];
                        int d = grid[r][r2];

                        int k = a+b+c+d;
                        int k2 = min(k,4-k);
                        cnt += k2;
                    }

                }
                l2++;
                r2--;
            }
            l++;
            r--;
        }

        cout<<cnt01<<cnt1p<<cnt0p;
        if(cnt1p%2==0){
            cnt += cnt01;
        }
        else{
            if(cnt01>0){
            cnt+=cnt01;
            }
            else{
                cnt+=2;
            }
        }
        return cnt;




    }
};
