class Solution {
    public int minFlips(int[][] matrix) {
        int rows = matrix.length;
        int cols = matrix[0].length;

        int totalFlips = 0;

        for (int row = 0; row < rows / 2; row++) {
            for (int col = 0; col < cols / 2; col++) {
                List<Integer> subMatrixElements = new ArrayList<>();
                subMatrixElements.add(matrix[row][col]);
                subMatrixElements.add(matrix[rows - row - 1][col]);
                subMatrixElements.add(matrix[row][cols - col - 1]);
                subMatrixElements.add(matrix[rows - row - 1][cols - col - 1]);

                int countOnes = 0;
                for (int element : subMatrixElements) {
                    countOnes += element;
                }

                totalFlips += Math.min(countOnes, 4 - countOnes);
            }
        }

        int midRowFlips = 0;
        int singleOnesCount = 0;

        if (rows % 2 != 0) {
            for (int col = 0; col < cols / 2; col++) {
                if (matrix[rows / 2][col] != matrix[rows / 2][cols - col - 1]) {
                    totalFlips++;
                    midRowFlips++;
                } else {
                    if (matrix[rows / 2][col] == 1) {
                        singleOnesCount++;
                    }
                }
            }
        }
        if (cols % 2 != 0) {
            for (int row = 0; row < rows / 2; row++) {
                if (matrix[row][cols / 2] != matrix[rows - row - 1][cols / 2]) {
                    totalFlips++;
                    midRowFlips++;
                } else {
                    if (matrix[row][cols / 2] == 1) {
                        singleOnesCount++;
                    }
                }
            }
        }

        if (rows % 2 != 0 && cols % 2 != 0 && matrix[rows / 2][cols / 2] == 1) {
            totalFlips++;
        }

        if (midRowFlips == 0 && singleOnesCount % 2 != 0) {
            totalFlips += 2;
        }

        return totalFlips;
    }
}
