class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;

        vector<int> s(3, 0);
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int sm = grid[n / 2][a] + grid[n / 2][b];
                s[sm] += 1;
                a += 1;
                b -= 1;
            }
        }
        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int ak = grid[a][m / 2] + grid[b][m / 2];
                s[ak] += 1;
                a += 1;
                b -= 1;
            }
        }
        if (s[2] % 2 == 1) {
            if (s[1] > 0) {
                ans += s[1];
            } else {
                ans += 2;
            }
        } else {
            ans += s[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int ak = a + b + c + d;

                if (ak != 0 && ak != 4) {
                    ans += min(ak, 4 - ak);
                }
            }
        }
         if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }

        return ans;
    }
};
