class Solution {
public:
    int cnt_even(vector<vector<int>>& grid,int m,int n)
    {
        int cnt=0;
        for(int i=0;i<m/2;i++)
        {
            for(int j=0;j<n/2;j++)
            {
                int zeros=0;
                zeros+=(grid[i][j]==0);
                zeros+=(grid[m-1-i][j]==0);
                zeros+=(grid[m-1-i][n-1-j]==0);
                zeros+=(grid[i][n-1-j]==0);
                cnt+=min(zeros,4-zeros);
            }
        }
        cout<<"cnt_even"<<cnt<<'\n';
        return cnt;
    }
    int cnt_odd(vector<vector<int>>& grid,int m,int n)
    {
        int cnt=0;
        int sum_row=0;
        int cnt_row=0;
        int sum_col=0;
        int cnt_col=0;
        if(n%2!=0)
        {
        for(int i=0;i<m/2;i++)
        {
            if(grid[i][n/2]!=grid[m-1-i][n/2])
            cnt_col++;
            else
            {
                if(grid[i][n/2]==1)
                sum_col++;
            }
        }
        }
        if(m%2!=0)
        {
        for(int j=0;j<n/2;j++)
        {
            if(grid[m/2][j]!=grid[m/2][n-1-j])
            cnt_row++;
            else
            {
                if(grid[m/2][j]==1)
                sum_row++;
            }
        }
        }
        if(m%2==1 && n%2==1)
        {
           cnt+=(grid[m/2][n/2]==1);
        }
        cnt+=cnt_row+cnt_col;
        if((sum_row+sum_col)%2==0)
        { 
            cout<<"cnt_odd"<<cnt<<'\n';
        return cnt;}
        else
        {
            if(cnt_row+cnt_col==0)
            cnt+=2;
            cout<<"cnt_odd"<<cnt<<'\n';
            return cnt;
        }

    }
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        ans+=cnt_even(grid,m,n);
        if(m%2==1||n%2==1)
        ans+=cnt_odd(grid,m,n);
        return ans;
    }
};
