class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int count=0;
        int m=grid.size();
        int n=grid[0].size();
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++) {
                if(grid[i][j]) count++;
            }
        }
        int ans=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int sum=grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                ans+=min(sum,4-sum);
            }
        }

        int itr=0;
        if(m%2){
            for(int j=0;j<n;j++){
              if(grid[m/2][j]) itr++;
            }
        }
        if(n%2){
            for(int j=0;j<m;j++){
              if(grid[j][n/2]) itr++;
            }
        }
        if(n%2 && m%2 && grid[m/2][n/2]) itr--;

         if(m%2){
         for(int i=0;i<n/2;i++){
            if(grid[m/2][i]!=grid[m/2][n-1-i]){
                if(itr%4==3 || itr%4==0) itr++;
                else itr--;
                ans++;
            }
         }
         }
         if(n%2){
         for(int i=0;i<m/2;i++){
            if(grid[i][n/2]!=grid[m-1-i][n/2]){
                if(itr%4==3 || itr%4==0) itr++;
                else itr--;
                ans++;
            }
         }
         }
         ans+=itr%4;
         return ans;
    }
};
