class Solution
{
public:
    int minFlips(vector<vector<int>> &grid, int ans = 0)
    {
        int n = size(grid), m = size(grid[0]);
        ans += grid[n / 2][m / 2] and ((n & 1) and (m & 1));

        vector<int> shift(3, 0);
        if (n & 1)
        {
            int a = 0, b = m - 1;
            while (a < b) shift[grid[n / 2][a++] + grid[n / 2][b--]]++;
        }

        if (m & 1)
        {
            int a = 0, b = n - 1;
            while (a < b) shift[grid[a++][m / 2] + grid[b--][m / 2]] ++;
        }

        if (shift[2] & 1)
        {
            if (shift[1] > 0) ans += shift[1];
            else ans += 2;
        }
        else ans += shift[1];

        for (int i = 0; i < n / 2; i++)
        {
            for (int j = 0; j < m / 2; j++)
            {
                int x = (grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][m - 1 - j]);
                if (x != 0 and x != 4) ans += min(x, 4 - x);
            }
        }

        return ans;
    }
};
