class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int ans = 0;
        for(int i = 0;i < m / 2;i++){
            for(int j = 0;j < n / 2;j++){
                int ones = 0;
                if(grid[i][j] == 1)
                    ones++;
                if(grid[i][n - j - 1] == 1)
                    ones++;
                if(grid[m - i - 1][j] == 1)
                    ones++;
                if(grid[m - i - 1][n - j -1] == 1)
                    ones++;
                ans += Math.min(ones, 4 - ones);
            }
        }
        if(m % 2 == 1 && n % 2 == 1)
            ans += grid[m / 2][n / 2];
        int freq01And10 = 0, freq11 = 0;
        if(m % 2 == 1){
            for(int j = 0;j < n / 2;j++){
                if(grid[m / 2][j] != grid[m / 2][n - j - 1])
                    freq01And10++;
                else if(grid[m / 2][j] == 1 && grid[m / 2][n - j - 1] == 1)
                    freq11++;
            }
        }
        if(n % 2 == 1){
            for(int i = 0;i < m / 2;i++){
                if(grid[i][n / 2] != grid[m - i - 1][n / 2])
                    freq01And10++;
                else if(grid[i][n / 2] == 1 && grid[m - i - 1][n / 2] == 1)
                    freq11++;
            }
        }
        if(freq11 % 2 == 1){
            if(freq01And10 != 0){
                freq01And10--;
                ans++;
            }
            else
                ans += 2;
        }
        ans += freq01And10;
        return ans;

