class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int cnt=0;
        int n=grid.size();
        int m=grid[0].size();
        int change=0,x=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                int temp=0;
                if(i!=n-1-i && j!=m-1-j){
                    if(grid[i][j]==1) temp++;
                    if(grid[n-1-i][j]==1) temp++;
                    if(grid[i][m-1-j]==1) temp++;
                    if(grid[n-1-i][m-1-j]==1) temp++;
                    if(temp==0 || temp==4) continue;
                    if(temp==3) {
                        cnt+=1;
                        grid[i][j]=1,grid[n-1-i][j]=1,grid[i][m-1-j]=1,grid[n-1-i][m-1-j]=1;
                        continue;
                    }
                    cnt+=temp;
                    grid[i][j]=0,grid[n-1-i][j]=0,grid[i][m-1-j]=0,grid[n-1-i][m-1-j]=0;
                    continue;
                }
                if(i==n-1-i && j==m-j-1) {
                    if(grid[i][j]==1) 
                    cnt++;
                    grid[i][j]=0;
                    continue;
                }
                temp=0;
                if(n%2==1 && m%2==1) {
                    if(i==n-1-i){
                        temp+=(grid[i][j]==1);
                        temp+=(grid[i][m-1-j]==1);
                        if(temp==1) change+=1; 
                        if(temp==2) x+=temp;
                    }
                    if(j==m-1-j){
                        temp+=(grid[i][j]==1);
                        temp+=(grid[n-1-i][j]==1);
                        if(temp==1) change+=1; 
                        if(temp==2) x+=temp;
                    }
                }
                else if(n%2==1){
                    temp+=(grid[i][j]==1);
                    temp+=(grid[i][m-1-j]==1);
                    if(temp==1) change+=1; 
                    if(temp==2) x+=temp;
                }
                else if(m%2==1) {
                    temp+=(grid[i][j]==1);
                    temp+=(grid[n-1-i][j]==1);
                    if(temp==1) change+=1; 
                    if(temp==2) x+=temp;
                }
            }
        }
        // cout<<cnt<<endl;
        change/=2;
        x/=2;
        // cout<<change<<" "<<x<<endl;
        cnt+=change;
        if(change==0) {
            if(x%4==2) cnt+=2; 
        }
        // ans+=cnt;
        // for(int i=0;i<n;i++){
        //     for(int j=0;j<m;j++){
        //         cout<<grid[i][j]<<" ";
        //     }
        //     cout<<endl;
        // }
        // c1=0;
        // ans+=min(4-c1%4,c1%4);
        // cout<<cnt<<endl;
        return cnt;
    }
};
