from typing import *


class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = 0

        for i in range(m//2):
            for j in range(n//2):
                s = grid[i][j] + grid[-i-1][j] + grid[i][-j-1] + grid[-i-1][-j-1]
                ans += min(4-s, s)

        twos = 0
        ones = 0
        if n%2 and m%2:
            ans += grid[m//2][n//2]

        if m%2:
            for i in range(n//2):
                s = grid[m//2][i] + grid[m//2][-i-1]
                if s==1:
                    ans += 1
                    ones += 1
                else:
                    twos += s
        if n%2:
            for i in range(m//2):
                s = grid[i][n//2] + grid[-i-1][n//2]
                if s==1:
                    ans += 1
                    ones += 1
                else:
                    twos += s
        twos = twos%4
        if twos==0: return ans
        elif twos == 2:
            if ones: return ans
            return ans + 2

        return ans
