class Solution {
public:
    int minFlips(vector<vector<int>>& grid) 
    {
        return makeGridPal(grid);    
    }
private:
    int makeGridPal(vector<vector<int>>& grid)
    {
        int n = grid.size();
        int m = grid[0].size();
        int mid2 = n/2;
        int mid = m/2;
        int changes = 0;
        vector<vector<int>> help(n,vector<int>(m,0));
        for(int j = 0;j<mid2+n%2;j++)
        {
            for(int i = 0; i <mid+m%2; i++)
            {   
                help[j][i]++;
                help[n-j-1][i]++;
                help[n-j-1][m-i-1]++;
                help[j][m-i-1]++;
            }
        }
        int onesTwo = 0;
        int onesTwoCh = 0;
        int onesFour = 0;
        int fours = 0;
        for(int j = 0;j<mid2+n%2;j++)
        {
            for(int i = 0; i <mid+m%2; i++)
            {
                int ones = oness(grid,j,i,n,m);
                //cout<<ones<<"["<<help[j][i]<<"] ";
                if(help[j][i]==1)
                {
                    changes+=min(ones,4-ones);
                }  
                else if(help[j][i]==2)
                {
                    onesTwo+=ones/2;
                    changes+=min(ones/2,2-ones/2);
                    if(ones==2)
                        onesTwoCh = 1;
                }
                else 
                {
                    changes+=ones/4;
                }
            }
            //cout<<"\n";
        }
        if(onesTwo%4==2&&onesTwoCh==0)
            changes+=2;
        /*
        for(auto v : help)
        {
            for( auto i : v)
            {
                cout<<i<<" ";
            }
            cout<<"\n";
        }*/
        return changes;
    }
    int oness(vector<vector<int>> &grid,int j, int i,int n, int m)
    {
        return grid[j][i]+grid[n-j-1][i]+grid[j][m-i-1]+grid[n-j-1][m-i-1];
    }
};
