class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int ans = 0;
        for(int i = 0; i <= m/2-1; i++) {
            for(int j = 0; j <= n/2-1; j++) {
                int cnt = 0;
                if(grid[i][j] == 1) {
                    ++cnt;
                }
                if(grid[m-1-i][j] == 1) {
                    ++cnt;
                }
                if(grid[i][n-1-j] == 1) {
                    ++cnt;
                }
                if(grid[m-1-i][n-1-j] == 1) {
                    ++cnt;
                }
                ans += min(cnt, 4-cnt);
            }
        }
        int difCnt = 0;
        int cnt1 = 0;
        if(m % 2 == 1) {
            for(int j = 0; j <= n/2-1; j++) {
                if(grid[m/2][j] != grid[m/2][n-1-j]) {
                    ++difCnt;
                }
                if(grid[m/2][j] == 1) {
                    ++cnt1;
                }
                if(grid[m/2][n-1-j] == 1) {
                    ++cnt1;
                }
            }
        }
        if(n % 2 == 1) {
            for(int i = 0; i <= m/2-1; i++) {
                if(grid[i][n/2] != grid[m-1-i][n/2]) {
                    ++difCnt;
                }
                if(grid[i][n/2] == 1) {
                    ++cnt1;
                }
                if(grid[m-1-i][n/2] == 1) {
                    ++cnt1;
                }
            }
        }
        int change = min(cnt1 % 4, 4 - cnt1 % 4);
        if(difCnt < change) {
            ans += change;
        }
        else {
            ans += difCnt;
        }
        if(m % 2 && n % 2) {
            ans += grid[m/2][n/2];
        }
        return ans;
    }
};
