class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int h =0,v=0;

        int ans =0;
        for(int i=0;i<n/2+!n%2;i++)
        {
            for(int j=0;j<m/2+!m%2;j++)
            {
                int sum = grid[i][j]+grid[i][m-1-j]+grid[n-i-1][m-1-j]+grid[n-i-1][j];

                if(sum==0 || sum==4) continue;

                else if(sum==1||sum==3){
                    ans+=1;
                }
                else if(sum==2)ans+=2;
            }
        }

        //cout<<ans<<endl;

         int mandatory1 =0,miss=0;

        if(m%2)
        {
            int j = m/2;

            for(int i=0;i<n/2+!n%2;i++)
            {
                if(grid[i][j]==grid[n-1-i][j] && grid[i][j])
                {
                    mandatory1+=2;
                }
                else if(grid[i][j]!=grid[n-1-i][j])
                {
                    miss++;
                }
            }


        }   

        //cout<<ans<<endl;

        if(n%2)
        {
             int i = n/2;
            //int mandatory1 =0,miss=0;
            for(int j=0;j<m/2+!m%2;j++)
            {
                if(grid[i][j]==grid[i][m-1-j] && grid[i][j])
                {
                    mandatory1+=2;
                }
                else if(grid[i][j]!=grid[i][m-1-j])
                {
                    miss++;
                }
            }

        }
        //cout<<mandatory1<<" "<<miss<<endl;
         if(mandatory1%4!=0 && miss==0)
         {
                ans+=2;
         }
        else
        {
            ans+=miss;
        }
        //cout<<ans<<endl;

        if(m%2 && n%2 && grid[n/2][m/2])ans++;

        return ans;     
    }
};
