class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int u = 0, d = m-1, l = 0, r = n-1;
        int ans = 0;
        int c1 = 0, dif = 0;
        if(m & 1){
            int i = m/2;
            for(int j=0; j<n/2; j++){
                if(grid[i][j] == grid[i][n-1-j] && grid[i][n-1-j] == 1) c1++;
                if(grid[i][j] != grid[i][n-1-j]) {dif++; ans++;}
            }
        }
        if(n & 1){
            int j = n/2;
            for(int i=0; i<m/2; i++){
                if(grid[i][j] == grid[m-1-i][j] && grid[m-1-i][j] == 1) c1++;
                if(grid[i][j] != grid[m-1-i][j]) {dif++; ans++;}
            }
        }
        if((c1 & 1) && dif == 0) ans += 2;
        // cout << c1 << ", " << dif << endl;
        if((n & 1) && (m & 1) && grid[m/2][n/2] == 1) ans++;
        while(u < d && l < r){
            for(int i=u; i<m/2; i++){
                int c = grid[i][l] + grid[i][r] + grid[m-1-i][l] + grid[m-1-i][r];
                if(c >= 2) ans += 4 - c;
                else ans += c;
            }
            l++, r--;
            for(int j=l; j<n/2; j++){
                int c = grid[u][j] + grid[d][j] + grid[u][n-1-j] + grid[d][n-1-j];
                if(c >= 2) ans += 4 - c;
                else ans += c;
            }
            u++, d--;
        }
        return ans;
    }
};
