class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for (int i = 0; i < (n / 2); i++)
        {
            for (int j = 0; j < (m / 2); j++)
            {
                int sum = grid[i][j] + grid[n - i - 1][j] + grid[i][m - j - 1]
                + grid[n - i - 1][m - j - 1];
                ans += min(sum, 4 - sum);
            }
        }

        if (n % 2 == 1 && m % 2 == 0)
        {
            int sum = 0;
            int ones = 0;
            for (int j = 0; j < (m / 2); j++)
            {
                if (grid[n / 2][j] != grid[n / 2][m - j - 1])
                    sum++;
                if (grid[n / 2][j])
                    ones++;
            }
            if (ones % 2 == 1)
            {
                if (sum == 0) sum += 2;
            }
            ans += sum;
        }
        else if (m % 2 == 1 && n % 2 == 0)
        {
            int sum = 0;
            int ones = 0;
            for (int i = 0; i < (n / 2); i++)
            {
                if (grid[i][m / 2] != grid[n - i - 1][m / 2])
                    sum++;
                if (grid[i][m / 2])
                    ones++;
            }
            if (ones % 2 == 1)
            {
                if (sum == 0) sum += 2;
            }
            ans += sum;
        }
        else if ((n % 2) && (m % 2))
        {
            int sum = 0;
            int ones = 0;
            for (int j = 0; j < (m / 2); j++)
            {
                if (grid[n / 2][j] != grid[n / 2][m - j - 1])
                    sum++;
                if (grid[n / 2][j])
                    ones++;
            }
            for (int i = 0; i < (n / 2); i++)
            {
                if (grid[i][m / 2] != grid[n - i - 1][m / 2])
                    sum++;
                if (grid[i][m / 2])
                    ones++;
            }
            if (ones % 2 == 1)
            {
                if (sum == 0) sum += 2;
            }

            ans += sum;

            if (grid[n / 2][m / 2])
                ans++;
        }
        return ans;
    }
};
