class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt = grid[i][j] + grid[n-i-1][j] + grid[i][m-j-1] + grid[n-i-1][m-j-1];
                ans+=min(cnt,4-cnt);
            }
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++)cout<<grid[i][j];
            cout<<'\n';
        }
        cout<<ans<<' ';
        int added1=0;
        int cnt1=0;
        if(n%2){
            for(int j=0;j<m/2;j++){
                int cnt=grid[n/2][j] +  grid[n/2][m-j-1];
                cnt1+=cnt;
                added1+=min(cnt,2-cnt);
            }
        }
        cout<<ans<<' ';
        int added2=0;
        int cnt2=0;
        if(m%2){
            // cout<<"YEss";
            for(int i=0;i<n/2;i++){
                int cnt=grid[i][m/2] +  grid[n-i-1][m/2];
                cnt2+=cnt;
                added2+=min(cnt,2-cnt);
            }
        }
        // cout<<added<<' '<<cnt1;
        int added = added1+added2;
        cnt1+=cnt2;
        if((cnt1+added)%4==2 && added<2){
            if(added==1)ans+=1;
            else ans+=2;
        }else{
            ans+=added;
        }
        cout<<ans<<' ';
        if(n%2 && m%2)ans+=grid[n/2][m/2];
        return ans;
    }
};
