class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();
        int flipsRequired = 0;

        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                flipsRequired += 1;
            }
        }

        vector<int> flipCounts(3, 0);

        if (rows % 2 == 1) {
            int leftIndex = 0, rightIndex = cols - 1;
            while (leftIndex < rightIndex) {
                int total = grid[rows / 2][leftIndex] + grid[rows / 2][rightIndex];
                flipCounts[total] += 1;
                leftIndex += 1;
                rightIndex -= 1;
            }
        }

        if (cols % 2 == 1) {
            int topIndex = 0, bottomIndex = rows - 1;
            while (topIndex < bottomIndex) {
                int total = grid[topIndex][cols / 2] + grid[bottomIndex][cols / 2];
                flipCounts[total] += 1;
                topIndex += 1;
                bottomIndex -= 1;
            }
        }

        if (flipCounts[2] % 2 == 1) {
            if (flipCounts[1] > 0) {
                flipsRequired += flipCounts[1];
            } else {
                flipsRequired += 2;
            }
        } else {
            flipsRequired += flipCounts[1];
        }

        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int topLeft = grid[i][j];
                int bottomLeft = grid[rows - 1 - i][j];
                int topRight = grid[i][cols - 1 - j];
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];

                int totalSum = topLeft + bottomLeft + topRight + bottomRight;

                if (totalSum != 0 && totalSum != 4) {
                    flipsRequired += min(totalSum, 4 - totalSum);
                }
            }
        }

        return flipsRequired;
    }
};
