class Solution {
    public int minFlips(int[][] grid) {
         int n=grid.length;
         int m=grid[0].length;
         int i=0,j=0,ans=0,c1=-1,c2=-1,c3=-1,c4=-1,c=-1;
         for(i=0;i<n/2;i++)
         {
              for(j=0;j<m/2;j++)
              {
                    c1=grid[i][j];
                    c2=grid[n-i-1][j];
                    c3=grid[i][m-j-1];
                    c4=grid[n-i-1][m-j-1];
                    c=c1+c2+c3+c4;
                    if(c==1 || c==3)
                    ans++;
                    else if(c==2)
                    ans=ans+2;
                    grid[i][j]=0;
                    grid[n-i-1][j]=0;
                    grid[i][m-j-1]=0;
                    grid[n-i-1][m-j-1]=0;
              }
         }
        // System.out.println(ans);
          c1=0;c2=0;c3=0;
         if(n%2==1)
         {
                for(i=0;i<m/2;i++)
                {
                    c=grid[n/2][i]+grid[n/2][m-i-1];
                    if(c==0)
                    c1++;
                    else if(c==1)
                    c2++;
                    else
                    c3++;
                }
         }
        // System.out.println(ans);
          if(m%2==1)
         {
                for(i=0;i<n/2;i++)
                {
                    c=grid[i][m/2]+grid[n-i-1][m/2];
                     if(c==0)
                    c1++;
                    else if(c==1)
                    c2++;
                    else
                    c3++;
                }
         }
         if(n%2==1 || m%2==1)
         {
          if(2*c3%4==0)
                ans+=c2;
                else
                {
                    if(c3%2==1 && c2==0)
                    ans+=2;
                    else
                    ans+=c2;
                }
         }
         if(n%2==1 && m%2==1)
         {
             if(grid[n/2][m/2]==1)
             ans++;
         }
         return ans;
    }
}
