class Solution {
public:
    int minFlips(vector<vector<int>>& matrix) {
       int n = matrix.size(), m = matrix[0].size();
        int res = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (matrix[n / 2][m / 2] == 1) {
                res += 1;
            }
        }

        vector<int> dhruv(3, 0);
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int cnt = matrix[n / 2][a] + matrix[n / 2][b];
                dhruv[cnt] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int cnt = matrix[a][m / 2] + matrix[b][m / 2];
                dhruv[cnt] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (dhruv[2] % 2 == 1) {
            if (dhruv[1] > 0) {
                res += dhruv[1];
            } else {
                res += 2;
            }
        } else {
            res += dhruv[1];
        }

        for (int it = 0; it < n / 2; ++it) {
            for (int jt = 0; jt < m / 2; ++jt) {
                int x = matrix[it][jt];
                int y = matrix[n - 1 - it][jt];
                int z = matrix[it][m - 1 - jt];
                int w = matrix[n - 1 - it][m - 1 - jt];

                int cnt = x+y+z+w;

                if (cnt != 0 && cnt != 4) {
                    res += min(cnt, 4 - cnt);
                }
            }
        }

        return res;
    }
};
