class Solution {
public:
    int n, m, k;

    bool valid(int i, int j)
    {
        if(i<0 || j<0 || i>=n || j>=m)
            return false;
        return true;
    }
    int dpH(int curr, vector<int>&sum, vector<int>&total, int cost, vector<vector<int>>&dp)
    {
        if(curr>=k)
        {
            if(cost%4==0)
                return 0;
            else
                return 1000000;
        }
        if(dp[curr][cost]!=-1)
            return dp[curr][cost];
        return dp[curr][cost]=min(total[curr]-sum[curr]+dpH(curr+1, sum, total, (cost+total[curr])%4, dp), sum[curr]+dpH(curr+1, sum, total,cost, dp));
    }

    int minFlips(vector<vector<int>>& grid) {
        n=grid.size();
        m=grid[0].size();
        vector<int>sum, total;
        int currSum, currTotal;
        int x,y;
        for(int i=0;i<(n+1)/2;i++)
        {   
            for(int j=0;j<(m+1)/2;j++)
            {   currSum=0;
                currTotal=0;
                x = n-1-i;
                y=m-1-j;
                if(valid(i, j))
                {
                    currSum+=grid[i][j];
                    currTotal++;
                }
                if(i!=x && valid(x, j))
                {
                    currSum+=grid[x][j];
                    currTotal++;                    
                }
                if(j!=y && valid(i, y))
                {
                    currSum+=grid[i][y];
                    currTotal++;                    
                }
                if(i!=x && j!=y && valid(x,y))
                {   currSum+=grid[x][y];
                    currTotal++;                

                }
                sum.push_back(currSum);
                total.push_back(currTotal);


            }

        }
        k=sum.size();
        // for(int i=0;i<k;i++)
        // {
        //     cout<<sum[i]<<" "<<total[i]<<endl;
        // }
        vector<vector<int>>dp(k+1,vector<int>(5, -1));

        return dpH(0, sum, total, 0, dp);
    }
};
