#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& asdf) {
        int zxc = asdf.size();
        int rty = asdf[0].size();
        int vbn = 0;

        if (zxc % 2 == 1 && rty % 2 == 1) {
            if (asdf[zxc / 2][rty / 2] == 1) {
                vbn += 1;
            }
        }

        vector<int> uio(3, 0);

        if (zxc % 2 == 1) {
            int hjk = 0, lmn = rty - 1;
            while (hjk < lmn) {
                int opq = asdf[zxc / 2][hjk] + asdf[zxc / 2][lmn];
                uio[opq] += 1;
                hjk++;
                lmn--;
            }
        }

        if (rty % 2 == 1) {
            int qaz = 0, wsx = zxc - 1;
            while (qaz < wsx) {
                int edc = asdf[qaz][rty / 2] + asdf[wsx][rty / 2];
                uio[edc] += 1;
                qaz++;
                wsx--;
            }
        }

        if (uio[2] % 2 == 1) {
            if (uio[1] > 0) {
                vbn += uio[1];
            } else {
                vbn += 2;
            }
        } else {
            vbn += uio[1];
        }

        for (int z = 0; z < zxc / 2; ++z) {
            for (int x = 0; x < rty / 2; ++x) {
                int y = asdf[z][x];
                int c = asdf[zxc - 1 - z][x];
                int v = asdf[z][rty - 1 - x];
                int b = asdf[zxc - 1 - z][rty - 1 - x];

                int n = y + c + v + b;

                if (n != 0 && n != 4) {
                    vbn += min(n, 4 - n);
                }
            }
        }

        return vbn;
    }
};
