class Solution {

public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size();
        int m = g[0].size();
        int ans = 0;
        int ce,re = n-1;
        int cs,rs = 0;
        int c=0;
        while(re>rs){
            cs = 0;
            ce = m-1;
            while(ce>cs){
                c=0;
                c+=g[rs][cs];
                c+=g[rs][ce];
                c+=g[re][cs];
                c+=g[re][ce];
                if(c==2)ans+=2;
                if(c==3||c==1)ans+=1;
                cs++;
                ce--;
            }
            re--;
            rs++;
            // cout<<c<<" ";
        }
        // cout<<ans;
        c=0;
        int p=0,q=0,a=0;;
        if((n&1)==1){
            cs=0;
            ce=m-1;
            rs=n/2;
            while(ce>cs){
                if(g[rs][cs]==1&&g[rs][ce]==1){
                    c+=2;
                }else if(g[rs][cs]!=g[rs][ce]){
                    p++;
                }
                ce--;
                cs++;
            }
        }
        if((m&1)==1){
            cs=0;
            ce=n-1;
            rs=m/2;
            while(ce>cs){
                if(g[ce][rs]==1&&g[cs][rs]==1){
                    a+=2;
                }else if(g[cs][rs]!=g[ce][rs]){
                    q++;
                }
                ce--;
                cs++;
            }
        }
        // c,p,   a,q;

        if((c+a)%4==0){
            ans+=p+q;
        }
        else{
            if(p+q==0)ans+=2;
            else{
                ans+=p+q;
            }
        }
        if(((n&1)==1)&&((m&1)==1)&&(g[n/2][m/2]==1)){
            // cout<<"yes";
            ans++;
        }

        return ans;
    }
};
