from typing import List


class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        ans = 0
        n = len(grid)
        m = len(grid[0])
        for i in range(n // 2):
            for j in range(m // 2):
                cnt = grid[i][j] + grid[-(i + 1)][j] + grid[i][-(j + 1)] + grid[-(i + 1)][-(j + 1)]
                ans += min(cnt, 4 - cnt)
        if n % 2 == 0 and m % 2 == 0:
            return ans
        if m % 2 == 0:
            tmp = 0
            cnt = 0
            for i in range(m // 2):
                if grid[n // 2][i] != grid[n // 2][-(i + 1)]:
                    tmp += 1
                else:
                    cnt += grid[n // 2][i]
            if tmp != 0:
                return ans + tmp
            elif cnt % 2 == 0:
                return ans
            else:
                return ans + 2
        if n % 2 == 0:
            tmp = 0
            cnt = 0
            for i in range(n // 2):
                if grid[i][m // 2] != grid[-(i + 1)][m // 2]:
                    tmp += 1
                else:
                    cnt += grid[i][m // 2]
            if tmp != 0:
                return ans + tmp
            elif cnt % 2 == 0:
                return ans
            else:
                return ans + 2
        ans += grid[n // 2][m // 2]
