class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n = len(grid)
        m = len(grid[0])

        golu = 0


        for i in range(n // 2):
            for j in range(m // 2):
                elements = [
                    grid[i][j],
                    grid[n - i - 1][j],
                    grid[i][m - j - 1],
                    grid[n - i - 1][m - j - 1]
                ]

                o = sum(elements)

                golu += min(o, 4 - o)

        po = 0
        co = 0

        if n % 2 != 0:
            for j in range(m // 2):
                if grid[n // 2][j] != grid[n // 2][m - j - 1]:
                    golu += 1
                    po += 1
                else:
                    if grid[n // 2][j] == 1:
                        co += 1

        if m % 2 != 0:
            for i in range(n // 2):
                if grid[i][m // 2] != grid[n - i - 1][m // 2]:
                    golu += 1
                    po += 1
                else:
                    if grid[i][m // 2] == 1:
                        co += 1

        if n % 2 != 0 and m % 2 != 0 and grid[n // 2][m // 2] == 1:
            golu += 1

        if po == 0 and co % 2 != 0:
            golu += 2

        return golu
