class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int a=0,s=0,o=0;
        // for(int i=0;i<v.size();i++){
        //     for(int j=0;j<v[i].size();j++){
        //         if(v[i][j]==1)o++;
        //     }
        // }
        for(int i=0;i<v.size()/2;i++){
            for(int j=0;j<v[i].size()/2;j++){
                int z=v[i][j]+v[i][v[0].size()-j-1]+v[v.size()-i-1][j]+v[v.size()-i-1][v[0].size()-j-1];
                if(z%4){
                    if(z%2)a+=z%2;
                    else a+=2;
                }
            }
        }
        if(v.size()%2){
            for(int i=0;i<v[0].size()/2;i++){
                if(v[v.size()/2][i]!=v[v.size()/2][v[0].size()-i-1])s++;
                else if(v[v.size()/2][i])o++;
            }
        }
        if(v[0].size()%2){
            for(int i=0;i<v.size()/2;i++){
                if(v[i][v[0].size()/2]!=v[v.size()-i-1][v[0].size()/2])s++;
                else if(v[i][v[0].size()/2])o++;
            }
        }
        if(v.size()%2&&v[0].size()%2&&v[v.size()/2][v[0].size()/2])a++;
        if(o%2){
            if(s>0)return s+a;
            else return s+a+2;
        }
        else return s+a;
    }
};
