class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n=v.size();
        int m=v[0].size();

        int ans=0;

        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int o=4,z=0;

                if(v[i][j]==0){
                    z++;
                }

                if(v[i][m-j-1]==0){
                    z++;
                }

                if(v[n-i-1][j]==0){
                    z++;
                }

                if(v[n-i-1][m-j-1]==0){
                    z++;
                }
                o=o-z;

                ans+=min(o,z);
            }
        }

        int ct=0;
        int ans1=0;

        if(n%2==1){
            for(int j=0;j<m/2;j++){
                if(v[n/2][j]==1){
                    ct++;
                }
                if(v[n/2][m-j-1]==1){
                    ct++;
                }

                if(v[n/2][j]!=v[n/2][m-j-1]){
                    ans1++;
                }
            }
        }

        if(m%2==1){
            for(int j=0;j<n/2;j++){
                if(v[j][m/2]==1) ct++;
                if(v[n-j-1][m/2]==1) ct++;

                if(v[j][m/2]!=v[n-j-1][m/2]){
                    ans1++;
                }
            }
        }

        if(n%2==1 && m%2==1 && v[n/2][m/2]==1){
            ans++;
        }

        if(ct%4==2){
            if(ans1==0){
                ans+=2;
            }
            else ans+=ans1;
        }
        else{
            ans+=ans1;
        }

        return ans;
    }
};
