class Solution {
public:

    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }

        vector<int> cnt(3, 0);
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int rem = grid[n / 2][a] + grid[n / 2][b];
                cnt[rem] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int rem = grid[a][m / 2] + grid[b][m / 2];
                cnt[rem] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (cnt[2] % 2 == 1) {
            if (cnt[1] > 0) {
                ans += cnt[1];
            } else {
                ans += 2;
            }
        } else {
            ans += cnt[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int rem = a + b + c + d;

                if (rem != 0 && rem != 4) {
                    ans += min(rem, 4 - rem);
                }
            }
        }

        return ans;
    }


};
