class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=size(grid), m=size(grid[0]), sum=0;
        if(n*m<4){
            for(int i=0; i<n; i++){
                for(int j=0; j<m; j++){
                    sum+=grid[i][j];
                }
            }
            return sum;
        }
        int p, q, r, s, k;
        for(int i=0; i<n/2; i++){
            int a=0, b=m-1;
            while(a<b){
                int x=0, y=0;
                x+=grid[i][a]?1:0;
                x+=grid[i][b]?1:0;
                x+=grid[n-1-i][a]?1:0;
                x+=grid[n-1-i][b]?1:0;
                y+=grid[i][a]?0:1;
                y+=grid[i][b]?0:1;
                y+=grid[n-1-i][a]?0:1;
                y+=grid[n-1-i][b]?0:1;
                a++, b--;
            sum+=min(x,y);
            }
        }
        k=sum;
        // cout<<sum<<" ";
        if(n%2){
            int a=0, b=m-1, set1=0, mis_m=0;
            while(a<b){
                if(grid[n/2][a]!=grid[n/2][b])mis_m++;
                else set1+=grid[n/2][a];
                a++, b--;
            }
            if(set1%2){
                if(mis_m)sum+=mis_m;
                else sum+=2;
            }
            else sum+=mis_m;
            p=set1, q=mis_m;
            // cout<<mis_m<<" "<<sum;
        }
        if(m%2){
            // cout<<"j";
            // cout<<"j";
            int a=0, b=n-1, set1=0, mis_m=0;
            while(a<b){
                if(grid[a][m/2]!=grid[b][m/2])mis_m++;
                else set1+=grid[a][m/2];
                a++, b--;
            }
            if(set1%2){
                if(mis_m)sum+=mis_m;
                else sum+=2;
            }
            else sum+=mis_m;
            r=set1, s=mis_m;
        }
        if(m%2 && n%2){
            p+=r, q+=s;
            if(p%2){
                if(q)k+=q;
                else k+=2;
            }
            else k+=q;
            k+=grid[n/2][m/2];
            return k;
        }
        return sum;
    }
};
