class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int m = grid.size();
        int n = grid[0].size();
        int up=0,down=m-1;
        while(up<down){
            int left=0,right=n-1;
            while(left<right){
                int num=grid[up][left]+grid[up][right]+grid[down][left]+grid[down][right];
                if(num<=2){
                    ans+=num;
                } 
                else if(num ==3){
                    ans+=1;
                }
                left++;
                right--;
            }
            up++;
            down--;
        }
        int temp =0;
        int ans1 = 0;
        if(m%2==1){
            int mid = m/2;
            int left=0,right=n-1;
            while(left<right){
                temp += grid[mid][left] + grid[mid][right];
                if(grid[mid][left]!=grid[mid][right]){
                    ans1 ++;
                }
                left++;
                right--;
            }

        }
        if(n%2==1){
            int mid = n/2;
            int left=0,right=m-1;
            while(left<right){
                temp += grid[left][mid] + grid[right][mid];
                if(grid[left][mid]!=grid[right][mid]){
                    ans1 ++;
                }
                left++;
                right--;
            }
        }
        if((temp+ans1)%4==2){
            ans+=(ans1==0?2:ans1);
        }
        else{
            ans+=ans1;
        }
        if(m%2&&n%2){
            ans+=grid[m/2][n/2];
        }
        return ans;
    }
};
