class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int n=grid.size(),m=grid[0].size();

        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int temp=0;
                if(grid[i][j]) temp++;
                if(grid[i][m-j-1])temp++;
                if(grid[n-i-1][j]) temp++;
                if(grid[n-i-1][m-j-1]) temp++;
                ans += min(temp,4-temp);
            }
        }
        int ones=0,zeros=0,temp=0;
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i] != grid[n/2][m-i-1]){
                    temp++;
                    ones+=2;
                    zeros+=2;
                }
                else if(grid[n/2][i]) ones+=2;
            }
        }

        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-i-1][m/2]){
                    temp++;
                    ones+=2;
                    zeros+=2;
                }
                else if(grid[i][m/2]) ones+=2;
            }
        }
        if(ones%4==0 || zeros>1) ans+=temp;
        else ans+=2;

        if(n%2 && m%2 && grid[n/2][m/2]) ans++;
        return ans;
    }
};
