class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }

        vector<int> shift(3, 0);
        if (n % 2 == 1) {
            int mid = m / 2;
            for (int j = 0; j < mid; ++j) {
                int sum = grid[n / 2][j] + grid[n / 2][m - j - 1];
                shift[sum]++;
            }
        }

        if (m % 2 == 1) {
            int mid = n / 2;
            for (int i = 0; i < mid; ++i) {
                int sum = grid[i][m / 2] + grid[n - i - 1][m / 2];
                shift[sum]++;
            }
        }

        if (shift[2] % 2 == 1) {
            if (shift[1] > 0) {
                ans += shift[1];
            } else {
                ans += 2;
            }
        } else {
            ans += shift[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - i - 1][j];
                int c = grid[i][m - j - 1];
                int d = grid[n - i - 1][m - j - 1];

                int sum = a + b + c + d;
                if (sum != 0 && sum != 4) {
                    ans += min(sum, 4 - sum);
                }
            }
        }

        return ans;
    }
};
