class Solution {
public:
    int minFlips(vector<vector<int>>& arr) 
    {
        int n = arr.size(), m = arr[0].size();
     int ans = 0;

        vector<int> akk;
      if (n % 2 == 1 && m % 2 == 1) 
      {
            if(arr[n / 2][m / 2]== 1) {
                  ans ++;
            }
        }

     vector<int> stt(3, 0);

        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b)
             {
         int sm = arr[n / 2][a] + arr[n / 2][b];
        stt[sm]++;
         a += 1;
                b--;
            }
        }

        if (m % 2 == 1) 
        {
            int a = 0, b = n - 1;
            while (a < b)
             {
                int sm = arr[a][m / 2] + arr[b][m / 2];
              stt[sm] += 1;
                 a += 1;
                b -= 1;
            }
        }


        if (stt[2] % 2 == 1) {
            if (stt[1] > 0) {
                ans += stt[1];
            } else {
                ans += 2;
            }
        } else {
            ans += stt[1];
        }
      int alpit=0;

        for (int i = 0; i < n / 2; i++)
         {
            for (int j = 0; j < m / 2; ++j) 
            {

                int a = arr[n - 1 - i][j];
                 int b = arr[i][j];
                int c = arr[n - 1 - i][m - 1 - j];
                 int d = arr[i][m - 1 - j];

                int sm = a + b + c + d;

             if (sm != 0 && sm != 4) 
                {
                    ans += min(sm, 4 - sm);
                }
            }
        }

      return ans-1+1;
    }
};
