class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        size_t sy = grid.size();
        size_t sx = grid[0].size();
        int ans = 0;
        for (size_t y = 0; y < sy / 2; ++y) {
            for (size_t x = 0; x < sx / 2; ++x) {
                int a = 0;
                if (grid[y][x] == 1)
                    ++a;
                if (grid[y][sx - x - 1] == 1)
                    ++a;
                if (grid[sy - y - 1][x] == 1)
                    ++a;
                if (grid[sy - y - 1][sx - x - 1] == 1)
                    ++a;
                if (a == 1 || a == 3)
                    ++ans;
                else if (a == 2)
                    ans += 2;
            }
        }
        bool a1 = false;
        bool a2 = false;
        if (sx % 2 != 0) {
            for (size_t y = 0; y < sy / 2; ++y) {
                if (grid[y][sx / 2] != grid[sy - y - 1][sx / 2]) {
                    ++ans;
                    a2 = true;
                } else if (grid[y][sx / 2] == 1 && grid[sy - y - 1][sx / 2] == 1)
                    a1 = !a1;
            }
        }
        if (sy % 2 != 0) {
            for (size_t x = 0; x < sx / 2; ++x) {
                if (grid[sy / 2][x] != grid[sy / 2][sx - x - 1]) {
                    ++ans;
                    a2 = true;
                } else if (grid[sy / 2][x] == 1 && grid[sy / 2][sx - x - 1] == 1)
                    a1 = !a1;
            }
        }
        if (a1 && !a2)
            ans += 2;
        if (sx % 2 != 0 && sy % 2 != 0 && grid[sy / 2][sx / 2] == 1)
            ++ans;
        return ans;
    }
};
