class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:

        ans = 0
        m = len(grid)
        n = len(grid[0])
        for i in range(m // 2):
            for j in range(n // 2):
                ones = grid[i][j] + grid[m - i - 1][j] \
                 + grid[i][n - j - 1] + grid[m - i - 1][n - j - 1]

                if ones == 2:
                    swaps = 2
                else:
                    swaps = ones % 2

                ans += swaps

        two = 0
        swaps = 0
        if m % 2 == 1:
            row = grid[m // 2]
            for i in range(n // 2):
                if row[i] == 1 and row[n - i - 1] == 1:
                    two += 2
                elif row[i] != row[n - i - 1]:
                    swaps += 1

        if n % 2 == 1:
            for i in range(m // 2):
                s = grid[i][n // 2] + grid[m - i - 1][n // 2]
                if s == 1:
                    swaps += 1
                elif s == 2:
                    two += 2

        ans += swaps
        if two % 4 != 0:
            if not swaps:
                ans += 2

        if m % 2 == 1 and n % 2 == 1:
            ans += grid[m // 2][n // 2]

        return ans
