class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n = len(grid)
        m = len(grid[0])

        ans1 = 0
        ans2 = 0

        ans = 0

        total_ones = 0

        for i in range((n)//2):
            for j in range((m)//2):

                x1 = grid[i][j]
                x2 = grid[i][m - j - 1]
                x3 = grid[n - i - 1][j]
                x4 = grid[n - i - 1][m - j - 1]

                ones = x1 + x2 + x3 + x4
                total_ones += min(ones, 4 - ones)

                ans += min(ones, 4 - ones)

        if m%2 == 1 and n%2 == 1:
            if grid[n//2][m//2] == 1:
                ans += 1



        changes = 0
        ones = 0
        if n%2 == 1:
            for i in range((m//2)):
                if grid[n//2][i] == grid[n//2][m - i - 1] == 1:
                    ones += 1
                elif grid[n//2][i] != grid[n//2][m - i - 1]:
                    changes += 1
        if m%2 == 1:
            for i in range((n//2)):
                if grid[i][m//2] == grid[n - i - 1][m//2] == 1:
                    ones += 1
                elif grid[i][m//2] != grid[n - i - 1][m//2]:
                    changes += 1
        if ones%2 == 0:
            ans += changes
        else:
            if changes > 0:
                ans += changes
            else:
                ans += 2
        return ans
