class Solution {
public:
    void sum(int a,int b){
        a=a+b;
    }
    int minFlips(vector<vector<int>>& grid) {
        int a=0,b=0;
        sum(a,b);
        int rowCount = grid.size();
        int colCount = grid[0].size();
        int flipsRequired = 0;

        if (rowCount % 2 == 1 && colCount % 2 == 1) {
            if (grid[rowCount / 2][colCount / 2] == 1) {
                flipsRequired += 1;
            }
        }

        vector<int> flipCount(3, 0);

        if (rowCount % 2 == 1) {
            int left = 0, right = colCount - 1;
            while (left < right) {
                int sum = grid[rowCount / 2][left] + grid[rowCount / 2][right];
                flipCount[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        if (colCount % 2 == 1) {
            int top = 0, bottom = rowCount - 1;
            while (top < bottom) {
                int sum = grid[top][colCount / 2] + grid[bottom][colCount / 2];
                flipCount[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        if (flipCount[2] % 2 == 1) {
            if (flipCount[1] > 0) {
                flipsRequired += flipCount[1];
            } else {
                flipsRequired += 2;
            }
        } else {
            flipsRequired += flipCount[1];
        }

        for (int i = 0; i < rowCount / 2; ++i) {
            for (int j = 0; j < colCount / 2; ++j) {
                int topLeft = grid[i][j];
                int bottomLeft = grid[rowCount - 1 - i][j];
                int topRight = grid[i][colCount - 1 - j];
                int bottomRight = grid[rowCount - 1 - i][colCount - 1 - j];

                int total = topLeft + bottomLeft + topRight + bottomRight;

                if (total != 0 && total != 4) {
                    flipsRequired += min(total, 4 - total);
                }
            }
        }

        return flipsRequired;
    }
};
