class Solution:
    def minFlips(self, g: List[List[int]]) -> int:
        m, n = len(g), len(g[0])
        res = c = 0
        for i in range(m // 2):
            for j in range(n // 2):
                f = [0, 0]
                f[g[i][j]] += 1
                if i != m - 1 - i:
                    f[g[m - 1 - i][j]] += 1
                if j != n - 1 - j:
                    f[g[i][n - 1 - j]] += 1
                    if i != m - 1 - i:
                        f[g[m - 1 - i][n - 1 - j]] += 1
                res += min(f)
        d = 0
        if m % 2:
            i = m // 2
            for j in range(n // 2):
                d += g[i][j]
                if g[i][j] != g[i][n - 1 - j]:
                    c += 1
                    res += 1
        if n % 2:
            j = n // 2
            for i in range(m // 2):
                d += g[i][j]
                if g[i][j] != g[m - 1 - i][j]:
                    c += 1
                    res += 1
            if m % 2 and g[m // 2][j]:
                res += 1
        if d % 2 and c == 0:
            res += 2
        return res
