class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int f=0;
        int m=grid.size();
        int n=grid[0].size();
        for(int i=0;i<=(int)(grid.size()/2)-1;i++){
            for(int j=0;j<=(int)(grid[0].size()/2)-1;j++){
                int sum=0;
                sum+=grid[i][j];
                sum+=grid[i][grid[0].size()-1-j];
                sum+=grid[grid.size()-1-i][j];
                sum+=grid[grid.size()-1-i][grid[0].size()-1-j];
                // cout<<sum<<endl;
                if(sum==1||sum==3){
                    f++;
                }
                else if(sum==2){
                    f+=2;
                }
            }
        }
        // cout<<f<<endl;
        if(m%2==0 && n%2==0){
            return f;
        }
        else if(m%2==1 && n%2==0){
            int pairs=0;
            int c=0;
            int i1=(grid.size()/2);
            int i2=(grid[0].size()/2);
            for(int j=0;j<=(int)(grid[0].size()/2)-1;j++){
                if(grid[i1][j]==grid[i1][grid[0].size()-1-j]){
                    if(grid[i1][j]==1){
                        pairs++;
                    }
                }
                else{
                    f++;
                    c++;
                }
            }
            if(pairs%2==1){
                if(c==0){
                    f+=2;
                }
            }
            return f;
        }
        else if(m%2==0 && n%2==1){
            int pairs=0;
            int c=0;
            int i1=(grid.size()/2);
            int i2=(grid[0].size()/2);
            for(int j=0;j<=(int)(grid.size()/2)-1;j++){
                if(grid[j][i2]==grid[grid.size()-1-j][i2]){
                    if(grid[j][i2]==1){
                       pairs++;
                    }
                }
                else{
                    f++;
                    c++;
                }
            }
            if(pairs%2==1){
                if(c==0){
                    f+=2;
                }
            }
            return f;
        }
        else{
            int pairs=0;
            int c=0;
            int i1=(grid.size()/2);
            int i2=(grid[0].size()/2);
            for(int j=0;j<=(int)(grid[0].size()/2)-1;j++){
                if(grid[i1][j]==grid[i1][grid[0].size()-1-j]){
                    if(grid[i1][j]==1){
                        pairs++;
                    }
                }
                else{
                    f++;
                    c++;
                }
            }
            for(int j=0;j<=(int)(grid.size()/2)-1;j++){
                if(grid[j][i2]==grid[grid.size()-1-j][i2]){
                    if(grid[j][i2]==1){
                        pairs++;
                    }
                }
                else{
                    f++;
                    c++;
                }
            }
            if(pairs%2==1){
                if(c==0){
                    f+=2;
                }
            }
            if(grid[i1][i2]==1){
                f++;
            }
            return f;
        }
    }
};
