package main

import (
	"math"
)

func minFlips(grid [][]int) int {
	rows := len(grid)
	cols := len(grid[0])
	result := 0

	if rows%2 == 1 && cols%2 == 1 {
		if grid[rows/2][cols/2] == 1 {
			result += 1
		}
	}

	counts := [3]int{}
	if rows%2 == 1 {
		left, right := 0, cols-1
		for left < right {
			sum := grid[rows/2][left] + grid[rows/2][right]
			counts[sum] += 1
			left++
			right--
		}
	}

	if cols%2 == 1 {
		top, bottom := 0, rows-1
		for top < bottom {
			sum := grid[top][cols/2] + grid[bottom][cols/2]
			counts[sum] += 1
			top++
			bottom--
		}
	}

	if counts[2]%2 == 1 {
		if counts[1] > 0 {
			result += counts[1]
		} else {
			result += 2
		}
	} else {
		result += counts[1]
	}

	for i := 0; i < rows/2; i++ {
		for j := 0; j < cols/2; j++ {
			val1 := grid[i][j]
			val2 := grid[rows-1-i][j]
			val3 := grid[i][cols-1-j]
			val4 := grid[rows-1-i][cols-1-j]

			total := val1 + val2 + val3 + val4

			if total != 0 && total != 4 {
				result += int(math.Min(float64(total), float64(4-total)))
			}
		}
	}

	return result
}
