class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int res=0;
        int n=grid.size(),m=grid[0].size();
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt=0;
                cnt+=grid[i][j];
                cnt+=grid[i][m-j-1];
                cnt+=grid[n-i-1][j];
                cnt+=grid[n-i-1][m-j-1];
                if(cnt==3||cnt==1)
                    res++;
                if(cnt==2)
                    res+=2;
            }
        }
        if(n%2==0 and m%2==0){
            return res;
        }
        if(n%2==1 and m%2==1){
            res+=grid[n/2][m/2]?1:0;
        }
        vector<int>v(3,0);
        if(n%2==1){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]^grid[n/2][m-i-1]){
                    v[1]++;
                }else if(grid[n/2][i]){
                    v[2]++;
                }else{
                    v[0]++;
                }
            }
        }
        if(m%2==1){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]^grid[n-i-1][m/2]){
                    v[1]++;
                }else if(grid[i][m/2]){
                    v[2]++;
                }else{
                    v[0]++;
                }
            }
        }
        res+=v[1];
        if(v[2]%2==1 and v[1]==0){
            res+=2;
        }
        return res;
    }
};
