class Solution:
    def minFlips(self, g: List[List[int]]) -> int:
        n, m = len(g), len(g[0])
        a = 0

        for i in range(n // 2):
            for j in range(m // 2):
                e = [g[i][j], g[n - i - 1][j], g[i][m - j - 1], g[n - i - 1][m - j - 1]]
                o = sum(e)
                a += min(o, 4 - o)

        p, c = 0, 0

        if n % 2 != 0:
            for j in range(m // 2):
                if g[n // 2][j] != g[n // 2][m - j - 1]:
                    a += 1
                    p += 1
                else:
                    if g[n // 2][j] == 1:
                        c += 1

        if m % 2 != 0:
            for i in range(n // 2):
                if g[i][m // 2] != g[n - i - 1][m // 2]:
                    a += 1
                    p += 1
                else:
                    if g[i][m // 2] == 1:
                        c += 1

        if n % 2 != 0 and m % 2 != 0 and g[n // 2][m // 2] == 1:
            a += 1

        if p == 0 and c % 2 != 0:
            a += 2

        return a
