class Solution {
    public int minFlips(int[][] grid) {
        int rows = grid.length;
        int cols = grid[0].length;
        int flips = 0;

        // Iterate over 2x2 sub-grids
        for (int i = 0; i < rows / 2; i++) {
            for (int j = 0; j < cols / 2; j++) {
                int[] values = {
                    grid[i][j],
                    grid[rows - i - 1][j],
                    grid[i][cols - j - 1],
                    grid[rows - i - 1][cols - j - 1]
                };

                int oneCount = 0;
                for (int value : values) {
                    if (value == 1) {
                        oneCount++;
                    }
                }

                flips += Math.min(oneCount, 4 - oneCount);
            }
        }

        int unbalanced = 0;
        int onesInMiddle = 0;

        // Handle the central row if the number of rows is odd
        if (rows % 2 != 0) {
            int middleRow = rows / 2;
            for (int j = 0; j < cols / 2; j++) {
                if (grid[middleRow][j] != grid[middleRow][cols - j - 1]) {
                    flips++;
                    unbalanced++;
                } else if (grid[middleRow][j] == 1) {
                    onesInMiddle++;
                }
            }
        }

        // Handle the central column if the number of columns is odd
        if (cols % 2 != 0) {
            int middleCol = cols / 2;
            for (int i = 0; i < rows / 2; i++) {
                if (grid[i][middleCol] != grid[rows - i - 1][middleCol]) {
                    flips++;
                    unbalanced++;
                } else if (grid[i][middleCol] == 1) {
                    onesInMiddle++;
                }
            }
        }

        // Handle the center element if both dimensions are odd
        if (rows % 2 != 0 && cols % 2 != 0 && grid[rows / 2][cols / 2] == 1) {
            flips++;
        }

        if (unbalanced == 0 && onesInMiddle % 2 != 0) {
            flips += 2;
        }

        return flips;
    }
}
