#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& matrix) {
        int rows = matrix.size(), cols = matrix[0].size();
        int flips = 0;

        if (rows % 2 == 1 && cols % 2 == 1) {
            flips += handleCentralCell(matrix, rows, cols);
        }

        vector<int> shiftCounter(3, 0);
        if (rows % 2 == 1) {
            processCentralRow(matrix, rows, cols, shiftCounter);
        }

        if (cols % 2 == 1) {
            processCentralCol(matrix, rows, cols, shiftCounter);
        }

        flips += calculateShiftFlips(shiftCounter);
        flips += processQuadrants(matrix, rows, cols);

        return flips;
    }

private:
    int handleCentralCell(vector<vector<int>>& matrix, int rows, int cols) {
        return matrix[rows / 2][cols / 2] == 1 ? 1 : 0;
    }

    void processCentralRow(vector<vector<int>>& matrix, int rows, int cols, vector<int>& shiftCounter) {
        int left = 0, right = cols - 1;
        while (left < right) {
            int sum = matrix[rows / 2][left] + matrix[rows / 2][right];
            shiftCounter[sum] += 1;
            left += 1;
            right -= 1;
        }
    }

    void processCentralCol(vector<vector<int>>& matrix, int rows, int cols, vector<int>& shiftCounter) {
        int top = 0, bottom = rows - 1;
        while (top < bottom) {
            int sum = matrix[top][cols / 2] + matrix[bottom][cols / 2];
            shiftCounter[sum] += 1;
            top += 1;
            bottom -= 1;
        }
    }

    int calculateShiftFlips(vector<int>& shiftCounter) {
        if (shiftCounter[2] % 2 == 1) {
            return shiftCounter[1] > 0 ? shiftCounter[1] : 2;
        } else {
            return shiftCounter[1];
        }
    }

    int processQuadrants(vector<vector<int>>& matrix, int rows, int cols) {
        int flips = 0;
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int val1 = matrix[i][j];
                int val2 = matrix[rows - 1 - i][j];
                int val3 = matrix[i][cols - 1 - j];
                int val4 = matrix[rows - 1 - i][cols - 1 - j];

                int total = val1 + val2 + val3 + val4;

                if (total != 0 && total != 4) {
                    flips += min(total, 4 - total);
                }
            }
        }
        return flips;
    }
};
