class Solution {
    int min(int a,int b){
        return a<b?a:b;
    }
    public int minFlips(int[][] grid) {
        int n=grid.length;
        int m=grid[0].length;
        int c=0;
        for (int i=0;i<n/2;i++){
            for (int j=0;j<m/2;j++){
                int s=grid[i][j]+grid[n-i-1][j]+grid[i][m-j-1]+grid[n-i-1][m-j-1];
                c+=min(s,4-s);
            }
        }
        int s=0;boolean mk=false;
        if (n%2==1){
            int t=n/2;
            for (int j=0;j<m/2;j++){
                if (grid[t][j]!=grid[t][m-j-1]){
                    c++;mk=true;
                }
                if (grid[t][j]==1){
                    s++;
                }
            }
        }
        if (m%2==1){
            int t=m/2;
            for (int j=0;j<n/2;j++){
                if (grid[j][t]!=grid[n-j-1][t]){
                    c++;mk=true;
                }
                if (grid[j][t]==1){
                    s++;
                }
            }
        }
        if (!mk && s%2==1){
            c+=2;
        }
        if (n%2==1&&m%2==1&&grid[n/2][m/2]==1){
            c++;
        }
        return c;
    }
}
