class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length, m = grid[0].length;
        boolean f[][] = new boolean[n][m];
        int ans = 0;
        int pp = 0;
        int ppp = 0;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (f[i][j]) continue;
                int a[][] = new int[][]{{i, j}, {i, m - 1 - j}, {n - 1 - i, j}, {n - 1 - i, m - 1 - j}};
                int cnt = 0;
                int cc = 0;
                for (var p : a) {
                    if (f[p[0]][p[1]]) continue;
                    ++cc;
                    f[p[0]][p[1]] = true;
                    cnt += grid[p[0]][p[1]];
                }
                if (cc == 4) {
                    ans += Math.min(cc - cnt, cnt);
                } else if (cc == 2) {
                    if (cnt == 1) {
                        ans += 1;
                        ++ppp;
                    }
                    else if(cnt == 2){
                        ++pp;
                    }
                } else if (cc == 1) {
                    ans += cnt;
                }
            }
        }
        if(pp % 2 == 0 || ppp != 0) {
            return ans;
        }
        return ans + 2;
    }
}
