class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(), m=grid[0].size();
        int tot=0;
        if(n%2==1 && m%2==1) {
            if(grid[n/2][m/2]==1) tot+=1;
        }
        vector<int>a(3, 0);
        if(n%2==1) {
            int x=0,y=m-1;
            while(x<y) {
                int sm=grid[n/2][x]+grid[n/2][y];
                a[sm] += 1;
                x+=1;
                y-=1;
            }
        }
        if(m%2==1) {
            int x= 0,y=n-1;
            while(x<y) {
                int sm=grid[x][m/2]+grid[y][m/ 2];
                a[sm]+=1;
                x+=1;
                y-=1;
            }
        }

        if(a[2]%2==1) {
            if(a[1]>0) tot+=a[1];
            else tot+= 2;
        } 
        else tot+=a[1];

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int sm = a + b + c + d;

                if (sm != 0 && sm != 4) {
                    tot+= min(sm, 4 - sm);
                }
            }
        }

        return tot;
    }
};
