class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m=len(grid)
        n=len(grid[0])
        a=grid
        ans=0
        for i in range(m//2):
            for j in range(n//2):
                ans+=min((a[i][j]==1)+(a[i][-j-1]==1)+(a[-i-1][j]==1)+(a[-i-1][-j-1]==1),(a[i][j]==0)+(a[i][-j-1]==0)+(a[-i-1][j]==0)+(a[-i-1][-j-1]==0))

        if m%2==1:
            if n%2==1:
                if a[m//2][n//2]==1:
                    ans+=1
                p=0
                add=False
                for i in range(m//2):
                    if a[i][n//2]==a[-i-1][n//2]:
                        if a[-i-1][n//2]==1:
                            p+=1
                    else:
                        ans+=1
                        add=True
                for j in range(n//2):
                    if a[m//2][j]==a[m//2][-j-1]:
                        if a[m//2][-j-1]==1:
                            p+=1
                    else:
                        ans+=1
                        add=True
                if p%2==1 and add==False:
                    ans+=2
            else:
                p=0
                add=False
                for j in range(n//2):
                    if a[m//2][j]==a[m//2][-j-1]:
                        if a[m//2][-j-1]==1:
                            p+=1
                    else:
                        ans+=1
                        add=True
                if p%2==1 and add==False:
                    ans+=2
        else:
            if n%2==1:
                p=0
                add=False
                for i in range(m//2):
                    if a[i][n//2]==a[-i-1][n//2]:
                        if a[-i-1][n//2]==1:
                            p+=1
                    else:
                        ans+=1
                        add=True
                if p%2==1 and add==False:
                    ans+=2
        return ans
