class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
            int ans = 0;
    int m = grid.size();
    int n = grid[0].size();
    //剩两个单独看
    for (int i = 0; i < m / 2; i++)
    {
        for (int j = 0; j < n / 2; j++)
        {
            int a1 = grid[i][j];
            int a2 = grid[m - 1 - i][j];
            int a3 = grid[i][n - 1 - j];
            int a4 = grid[m - 1 - i][n - 1 - j];
            int sum = a1 + a2 + a3 + a4;
            if (sum == 1)ans += 1;
            if (sum == 2) 
            {

                ans += 2;

            }
            if (sum == 3)
            {

                ans += 1;
            }
        }
    }
    int sum_one = 0;
    int change = 0;
    if (m % 2 == 1)
    {
        int i = m / 2;
        for (int j = 0; j < n / 2; j++)
        {
            if (grid[i][j] != grid[i][n - 1 - j])
            {

                change++;
                ans += 1;

            }
            else if (grid[i][j] == 1)
            {
                sum_one += 2;
            }
        }
    }

    if (n % 2 == 1)
    {
        int j = n/ 2;
        for (int i = 0; i < m / 2; i++)
        {
            if (grid[i][j] != grid[m - 1 - i][j])
            {
                change++;
                ans += 1;

            }
            else if (grid[i][j] == 1)
            {
                sum_one += 2;
            }
        }

    }
if (sum_one % 4 != 0&&change==0)ans += 2;  
    if (m % 2 == 1 && n % 2 == 1)
    {
        if (grid[m / 2 ][n / 2] == 1)ans++;
    }

    return ans;
    }
};
