class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        int flag=0;
        int opt=0;
        for(int row=0; row<(m+1)/2 ;row++)
        {
            for(int col=0;col<(n+1)/2 ;col++)
            {

                vector<int> rows = { row, m - 1 - row };
                vector<int> cols = { col, n - 1 - col };
                if (row == m - 1 - row) rows.pop_back(); 
                if (col == n - 1 - col) cols.pop_back();
                vector<int>val(2,0);
                for(int r:rows)
                {
                    for(int c:cols)
                    {
                        val[grid[r][c]]++;
                    }
                }




                if(row==m-1-row && col==n-1-col && grid[row][col]==1) ans++;
                else if(row==m-1-row)
                {
                    if(grid[row][col]==grid[row][n-1-col] && grid[row][col]==1) flag=1-flag;
                    else if(grid[row][col]!=grid[row][n-1-col]) 
                    {
                        opt=1;
                        ans++;
                    }
                }
                else if(col==n-1-col)
                {
                    if(grid[row][col]==grid[m-1-row][col] && grid[row][col]==1) flag=1-flag;
                    else if(grid[row][col]!=grid[m-1-row][col])
                    {
                        opt=1;
                        ans++;
                    } 
                }
                else ans+=min(val[0],val[1]);

            }
        }



        if(flag && !opt) return ans+2;
        else return ans;
    }
};
