class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int cnt = 0, cnt1 = 0, cnt2 = 0, n = grid.size(), m = grid[0].size();
        for(int i = 0; i < n/2; i++)
            for(int j = 0; j < m/2; j++)
            {
                int aux = 0;
                aux += grid[i][j];
                aux += grid[i][m-j-1];
                aux += grid[n-i-1][j];
                aux += grid[n-i-1][m-j-1];
                if(aux % 4 != 0)
                    cnt += min(aux, 4-aux);
            }
        if(n % 2)
        {
            for(int i = 0; i < m/2; i++)
            {
                int aux = 0;
                aux += grid[n/2][m-i-1];
                aux += grid[n/2][i];
                if(aux % 2 == 0)
                    cnt2 += aux;
                else
                    cnt1++;
            }
        }
        if(m % 2)
        {
            for(int i = 0; i < n/2; i++)
            {
                int aux = 0;
                aux += grid[n-i-1][m/2];
                aux += grid[i][m/2];
                if(aux % 2 == 0)
                    cnt2 += aux;
                else
                    cnt1++;
            }
        }
        if(n % 2 && m % 2 && grid[n/2][m/2] == 1)
            cnt++;
        if(cnt2 % 4 == 2 && cnt1)
            return (cnt+cnt1);
        else if(cnt2 % 4 == 2 && !cnt1)
            return (cnt+2);
        else
            return (cnt+cnt1);
    }
};
